const initState = {
    user: {
        _id:'KouatchouaWorld',
        matricule:'17C12NBW700',
        nom:'Kouatchoua',
        prenom:'Mark',
        email:'kouatch@gmail.com',
        tel:'679879615',
        role:'Enseignant',
        startDate:'01/04/2020',
    }
}

const userReducer = (state = initState, action={})=>{
    switch(action.type){
        case 'SET_USER':
            return{...state, user:[...state.user, ...action.payload]}
        default:
            return state
    }
}

export default userReducer;
